import { Container, Box } from 'theme-ui';
import { ThemeProvider } from 'theme-ui';
import { StickyProvider } from '../contexts/app/app.provider';
import theme from 'theme';
import SEO from 'components/seo';
import Layout from 'components/layout';
import Head from 'next/head';
import Link from 'next/link';
//import Date from '../components/date';
import BlogSection from '../sections/blog-section';
import BannerImg from 'assets/banner.png';

import { getSortedPostsData } from '../../lib/posts';

export async function getStaticProps() {
  const allPostsData = await getSortedPostsData();
  return {
    props: {
      allPostsData,
    },
  };
}

export default function Blog({allPostsData}) {
  return (
    // <ThemeProvider theme={theme}>
    //   <StickyProvider>
        <Layout>
          <SEO title="Datagency" />
          <section sx={styles.banner} id="home">
      <Container sx={styles.banner.container}>
        <Box sx={styles.banner.contentBox}>
          <Heading as="h1" variant="heroPrimary">
            Datagency is your trusted data analytics partner
          </Heading>
          <Text as="p" variant="heroSecondary">
          We provide data analytics consulting and implementation services to grow your business
          </Text>
          <Button variant="primary">Start Your Journey</Button>
        </Box>
      </Container>
    </section>
            <section sx={styles.blog} >
              <Container sx={styles.blog.container}>
                <Box sx={styles.blog.contentBox}>
                  <h2>Blog</h2>
                  <ul>
                  {allPostsData.map(({ id, date, title }) => (
                    <li>
                    <Link href={`/blog/${id}`}>
                      <a>{title}</a>
                    </Link>
                    <br />
                    <small>
                      <Date dateString={date} />
                    </small>
                  </li>
                  ))}
                  </ul>
                </Box>
              </Container>
            </section>
         </Layout>
    //   </StickyProvider>
    // </ThemeProvider>
  );
}

const styles = {
  blog: {
    pt: ['140px', '145px', '155px', '170px', null, null, '180px', '215px'],
    pb: [2, null, 0, null, 2, 0, null, 5],
    position: 'absolute',
    zIndex: 2,
    backgroundImage: `url(${BannerImg})`,
    backgroundSize: 'cover',
  },
  container: {
    minHeight: 'inherit',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
  },
  contentBox: {
    width: ['100%', '90%', '535px', null, '57%', '60%', '68%', '60%'],
    mx: 'auto',
    textAlign: 'center',
    mb: ['40px', null, null, null, null, 7],
  },
}
